return $input.all().map(item => {
  const avg = item.json.avg_monthly_searches;

  let band = "unknown";
  if (avg <= 10) band = "very_low";
  else if (avg <= 100) band = "low";
  else if (avg <= 1000) band = "medium";
  else if (avg <= 10000) band = "high";
  else band = "very_high";

  return {
    json: {
      ...item.json,
      search_vol_band: band
    }
  };
});

const output = {};

for (const item of items) {
  const key = item.json.search_vol_band+"_band";
  const value = item.json.count_search_vol_band.toString(); // ensure it's a string
  output[key] = value;
}

return [{ json: output }];

return items.map(item => {
  const data = item.json;

  const low = parseInt(data.LOW_competition || 0);
  const medium = parseInt(data.MEDIUM_competition || 0);
  const unspecified = parseInt(data.UNSPECIFIED_competition || 0);
  const total = parseInt(data.count_keyword || 0);

  const veryLowBand = parseInt(data.very_low_band || 0);
  const lowBand = parseInt(data.low_band || 0);
  const mediumBand = parseInt(data.medium_band || 0);
  const highBand = parseInt(data.high_band || 0);

  const unspecifiedRatio = total > 0 ? unspecified / total : 0;
  const opportunityRatio = total > 0 ? (low + medium) / total : 0;
  const veryLowRatio = total > 0 ? veryLowBand / total : 0;

  const unspecified_ratio_threshold = $('Thresholds').first().json.unspecified_ratio_threshold
  const very_low_vol_ratio_threshold = $('Thresholds').first().json.very_low_vol_ratio_threshold
  const oppty_ratio_threshold = $('Thresholds').first().json.oppty_ratio_threshold
  const kw_vol_threshold = $('Thresholds').first().json.kw_vol_threshold

  let label = "opportunity"; // default

  if (total < kw_vol_threshold) {
    label = "insufficient";
  } else if (unspecifiedRatio >= unspecified_ratio_threshold && total >= kw_vol_threshold) {
    label = "unknown_opportunity";
  } else if (unspecifiedRatio >= unspecified_ratio_threshold && total < kw_vol_threshold) {
    label = "unknown_risky";
  } else if (veryLowRatio >= very_low_vol_ratio_threshold) {
    label = "low_volume_risky";
  } else if (opportunityRatio >= oppty_ratio_threshold) {
    label = "opportunity";
  }

  return {
    json: {
      ...data,
      topic_validity: label,
      unspecifiedRatio: unspecifiedRatio,
      opportunityRatio: opportunityRatio,
      veryLowRatio: veryLowRatio
    }
  };
});
